"use strict";

class URLMalformedError extends Error {
    constructor(message) {
	super(message);
	this.name = this.constructor.name;
	this.message = message; 
	Error.captureStackTrace(this, this.constructor.name);
    }
}

module.exports = URLMalformedError

"use strict";

const URLMalformedError = require('./URLMalformedError')

let specialSchemas = {
    "ftp": 21,
    "file": null,
    "gopher": 70,
    "http": 80,
    "https": 443,
    "ws": 80,
    "wss": 443,
    // local schemas
    "about": null,
    "blob": null,
    "data": null, 
    "filesystem": null,
    // reThink specific schemas
    "user": null,
    "ctxt": null,
    "acct": null,
    "user-uuid": null,
    "domain": null,
    "hyperty": null,
    "hyperty-catalogue": null,
    "hyperty-runtime": null,
    "hyperty-runtime-uuid": null,
    "hyperty-runtime-dev-os": null,
    "hyperty-runtime-imei": null,
    "hyperty-runtime-esn": null,
    "hyperty-runtime-meid": null,
    "comm": null
};


class URLDataObject {
    /**
     * Creates the Catalogue Data Object
     * @param scheme - indicates the scheme of the URL 
     * @param host - indicates the type of Catalogue Data Object e.g. Hyperty, ProtocolStub, etc
     * @param port - human-understandable name of the catalogue object e.g. "My Awesome Hyperty"
     * @param path - description of the source package
     */
    constructor(scheme, host, port, path, parameters) {  
	this.scheme = scheme;
	this.host = host;
	this.port = port;
	this.path = path;
	this.parameters = parameters
    }

    get scheme(){
	return this._scheme;
    }  

    get host(){
	return this._host;
    }

    get port(){
	return this._port;
    }  

    get path(){
	return this._path;
    }  
    
    get url(){
        this._url = this._scheme + "://" + this._host;
	
        if (this._port) {
	    this._url = this._url + ":" + this._port;
	}
	
	if (this._path) {
	    this._url = this._url + "/" + this._path;	
	}
        return this._url; 
    }

    get parameters() {
	return this._parameters
    }

    set scheme(value) {
	if (!specialSchemas.hasOwnProperty(value)) {
	    throw new URLMalformedError("schema not found");
	}
	this._scheme=value;
    }
    
    set host(value) {
	this._host=value;
    }

    set port(value) {
	if (isNaN(parseInt(value))) {
	    this._port = specialSchemas[this.scheme];
	} else {
	    this._port = value;
	}
    }// set port
    
    set path(value) {
	this._path=value;
    }

    set parameters(value) {
	this._parameters = value;
    }
}

module.exports = URLDataObject

/**
 * @author alice.cheambe[at]fokus.fraunhofer.de
 * The CatalogueDataObject class is the representation of the reTHINK Catalogue Data Model
 */
class CatalogueDataObject {
    /**
     * Creates the Catalogue Data Object
     * @param guid - Global Unique identifier of the Catalogue Object (e.g. Hyperty descriptor, ProtocolStub descriptor,
     * etc) enabling the same object to be stored and discovered in different Catalogues. That means, guid corresponds to
     * [resource-type-id] per BNF of Resource Path. Couldn't we have problems with too long URL paths?
     * @param type - indicates the type of Catalogue Data Object e.g. Hyperty, ProtocolStub, etc
     * @param objectName - human-understandable name of the catalogue object e.g. "My Awesome Hyperty"
     * @param description - description of the source package
     * @param language - the programming language used in the SourcePackage.SourceCode
     * @param sourcePackageURL - A string containing the URL from where the source code package of the corresponding
     * catalogue object, e.g. deployable packages containing executable code for Hyperties or ProtoStubs, can be downloaded
     */
    constructor(guid, type, objectName, description, language, sourcePackageURL) {
        this._guid = guid;
        this._type = type;
        this._objectName = objectName;
        this._description = description;
        this._language = language;
        this._sourcePackageURL = sourcePackageURL;

        this._signature = null;
        this._sourcePackage = null;
    }

    // Getters
    get guid() {
        return this._guid;
    }

    get type() {
        return this._type;
    }

    get objectName() {
        return this._objectName;
    }

    get description() {
        return this._description;
    }

    get language() {
        return this._language;
    }

    get signature() {
        return this._signature;
    }

    get sourcePackage() {
        return this._sourcePackage;
    }

    get sourcePackageURL() {
        return this._sourcePackageURL;
    }


    // Setters
    /**
     * Set the signature to enables integrity and authenticity verification
     * @param signature
     */
    set signature(signature) {
        if (signature)
            this._signature = signature;
    }

    set sourcePackage(sourcePackage) {
        if (sourcePackage)
            this._sourcePackage = sourcePackage;
    }

    set guid(guid) {
        if (guid)
            this._guid = guid;
    }

    set type(type) {
        if (type)
            this._type = type;
    }

    set objectName(objectName) {
        if (objectName)
            this._objectName = objectName;
    }

    set description(description) {
        if (description)
            this._description = description;
    }

    set language(language) {
        if (language)
            this._language = language;
    }

    set sourcePackageURL(sourcePackageURL) {
        if (sourcePackageURL)
            this._sourcePackageURL = sourcePackageURL;
    }
}

export const CatalogueObjectType = {
    HYPERTY: 'hyperty', PROTOSTUB: 'protostub', HYPERTY_RUNTIME: 'hyperty_runtime',
    HYPERTY_INTERCEPTOR: 'hyperty_inspector', HYPERTY_DATA_OBJECT: 'hyperty_data_object',
    POLICY_ENFORCER: 'policy_enforcer', DATA_SCHEMA: 'data_schema'
};
export const DataObjectSourceLanguage = {
    JAVASCRIPT_ECMA6: 'javascript_ecma6', JAVASCRIPT_ECMA5: 'javascript_ecma5',
    JSON_SCHEMA_V4: 'json_schema_v4', PYTHON: 'python', TYPESCRIPT: 'typescript'
};
export default CatalogueDataObject;

import RethinkObject from '../reTHINKObject/RethinkObject';

import CatalogueDataObject from './CatalogueDataObject';
import {CatalogueObjectType} from './CatalogueDataObject';
import {DataObjectSourceLanguage} from './CatalogueDataObject';

import SourcePackage from './SourcePackage';

import HypertyDescriptor from './HypertyDescriptor';
import {HypertyType} from './HypertyDescriptor';

import ProtocolStubDescriptor from './ProtocolStubDescriptor';

import HypertyRuntimeDescriptor from './HypertyRuntimeDescriptor';
import {RuntimeType} from './HypertyRuntimeDescriptor';
import {RuntimeProtocolCapabilityType} from './HypertyRuntimeDescriptor';
import {RuntimeHypertyCapabilityType} from './HypertyRuntimeDescriptor';

import PolicyEnforcerDescriptor from './PolicyEnforcerDescriptor';
import DataObjectSchema from './DataObjectSchema';

class CatalogueDataObjectFactory extends RethinkObject {

    /**
     * Constructor
     * @param {boolean} validation
     * @param {URL.URL } schema - link to the schema
     */
    constructor(validation, schema) {
        super(validation, schema);
    }

    /**
     * Create CatalogueDataObject
     * @param {string} guid - Catalogue Global Unique identifier of the Catalogue Object
     * @param {CatalogueObjectType} type - Indicates the type of Catalogue Data Object
     * @param {string} objectName - Human-understandable name of the catalogue object
     * @param {string} description
     * @param {DataObjectSourceLanguage} language - Programming language used in the SourcePackage
     * @param {string} sourcePackageURL - URL from where the source code package of the corresponding catalogue object can be downloaded.
     * @returns {CatalogueDataObject}
     */
    createCatalogueDataObject(guid, type, objectName, description, language, sourcePackageURL) {
        if (
            typeof guid === "undefined"
            || typeof type === "undefined"
            || typeof objectName === "undefined"
            || typeof description === "undefined"
            || typeof language === "undefined"
            || typeof sourcePackageURL === "undefined"
        )
            throw new Error("Invalid parameters!");
        return new CatalogueDataObject(guid, type, objectName, description, language, sourcePackageURL);
    }

    /**
     * Create HypertyDescriptor
     * @param {string} guid - Catalogue Global Unique identifier of the Catalogue Object
     * @param {string} objectName - Human-understandable name of the catalogue object
     * @param {string} description
     * @param {DataObjectSourceLanguage} language - Programming language used in the SourcePackage
     * @param {string} sourcePackageURL - URL from where the source code package of the corresponding catalogue object can be downloaded.
     * @param {int} hypertyType - A tag that identifies what type of hyperty is described in the object.
     * @param {URL.URLList} dataObjects - Defines the Data Object Schemas supported by the Hyperty through a list of Catalogue URLs from where these schemas can be reached.
     * @returns {HypertyDescriptor}
     */
    createHypertyDescriptorObject(guid, objectName, description, language, sourcePackageURL, hypertyType,
                                  dataObjects) {
        if (
            typeof guid === "undefined"
            || typeof objectName === "undefined"
            || typeof description === "undefined"
            || typeof language === "undefined"
            || typeof sourcePackageURL === "undefined"
            || typeof hypertyType === "undefined"
            || typeof dataObjects === "undefined"
        )
            throw new Error("Invalid parameters!");
        return new HypertyDescriptor(guid, CatalogueObjectType.HYPERTY, objectName, description,
            language, sourcePackageURL, hypertyType, dataObjects);
    }

    /**
     * Create ProtocolStubDescriptor
     * @param {string} guid - Catalogue Global Unique identifier of the Catalogue Object
     * @param {string} objectName - Human-understandable name of the catalogue object
     * @param {string} description
     * @param {DataObjectSourceLanguage} language - Programming language used in the SourcePackage
     * @param {string} sourcePackageURL - URL from where the source code package of the corresponding catalogue object can be downloaded.
     * @param {URL.URL} messageSchemas - Defines the Schema describing the Message Data Model used by the Hyperty through the Catalogue URL from where the Message schema can be reached. If not defined, by default it is assumed the standard Message Model is used.
     * @param configuration - Data required to configure the ProtocolStub
     * @param constraints - Describes capabilities required from the Hyperty Runtime in order to be able to execute the ProtocolStub
     * @returns {ProtocolStubDescriptor}
     */
    createProtoStubDescriptorObject(guid, objectName, description, language, sourcePackageURL, messageSchemas,
                                    configuration, constraints) {
        if (
            typeof guid === "undefined"
            || typeof objectName === "undefined"
            || typeof description === "undefined"
            || typeof language === "undefined"
            || typeof sourcePackageURL === "undefined"
            || typeof messageSchemas === "undefined"
            || typeof configuration === "undefined"
            || typeof constraints === "undefined"
        )
            throw new Error("Invalid parameters!");
        return new ProtocolStubDescriptor(guid, CatalogueObjectType.PROTOSTUB, objectName, description,
            language, sourcePackageURL, messageSchemas, configuration, constraints);
    }

    /**
     * Create HypertyRuntimeDescriptor
     * @param {string} guid - Catalogue Global Unique identifier of the Catalogue Object
     * @param {string} objectName - Human-understandable name of the catalogue object
     * @param {string} description
     * @param {DataObjectSourceLanguage} language - Programming language used in the SourcePackage
     * @param {string} sourcePackageURL - URL from where the source code package of the corresponding catalogue object can be downloaded.
     * @param runtimeType
     * @param hypertyCapabilities - Supported capabilities to execute Hyperties
     * @param protocolCapabilities - Supported capabilities to execute Protocol Stubs
     * @returns {HypertyRuntimeDescriptor}
     */
    createHypertyRuntimeDescriptorObject(guid, objectName, description, language, sourcePackageURL,
                                         runtimeType, hypertyCapabilities, protocolCapabilities) {
        if (
            typeof guid === "undefined"
            || typeof objectName === "undefined"
            || typeof description === "undefined"
            || typeof language === "undefined"
            || typeof sourcePackageURL === "undefined"
            || typeof runtimeType === "undefined"
            || typeof hypertyCapabilities === "undefined"
            || typeof protocolCapabilities === "undefined"
        )
            throw new Error("Invalid parameters!");

        return new HypertyRuntimeDescriptor(guid, CatalogueObjectType.HYPERTY_RUNTIME, objectName,
            description, language, sourcePackageURL, runtimeType, hypertyCapabilities, protocolCapabilities);
    }

    /**
     * Create PolicyEnforcerDescriptor
     * @param {string} guid - Catalogue Global Unique identifier of the Catalogue Object
     * @param {string} objectName - Human-understandable name of the catalogue object
     * @param {string} description
     * @param {DataObjectSourceLanguage} language - Programming language used in the SourcePackage
     * @param {string} sourcePackageURL - URL from where the source code package of the corresponding catalogue object can be downloaded.
     * @param configuration
     * @param policies
     * @returns {PolicyEnforcerDescriptor}
     */
    createPolicyEnforcerDescriptorObject(guid, objectName, description, language, sourcePackageURL, configuration,
                                         policies) {
        if (
            typeof guid === "undefined"
            || typeof objectName === "undefined"
            || typeof description === "undefined"
            || typeof language === "undefined"
            || typeof sourcePackageURL === "undefined"
            || typeof configuration === "undefined"
            || typeof policies === "undefined"
        )
            throw new Error("Invalid parameters!");

        return new PolicyEnforcerDescriptor(guid, CatalogueObjectType.POLICY_ENFORCER, objectName,
            description, language, sourcePackageURL, configuration, policies);
    }

    /**
     * Create DataObjectSchema
     * @param {string} guid - Catalogue Global Unique identifier of the Catalogue Object
     * @param {string} objectName - Human-understandable name of the catalogue object
     * @param {string} description
     * @param {DataObjectSourceLanguage} language - Programming language used in the SourcePackage
     * @param {string} sourcePackageURL - URL from where the source code package of the corresponding catalogue object can be downloaded.
     * @returns {DataObjectSchema}
     */
    createDataObjectSchema(guid, objectName, description, language, sourcePackageURL) {
        if (
            typeof guid === "undefined"
            || typeof objectName === "undefined"
            || typeof description === "undefined"
            || typeof language === "undefined"
            || typeof sourcePackageURL === "undefined"
        )
            throw new Error("Invalid parameters!");

        return new DataObjectSchema(guid, CatalogueObjectType.DATA_SCHEMA, objectName, description, language, sourcePackageURL);
    }

    /**
     * Create SourcePackage
     * @param sourceCodeClassname - The Class-name of the SourceCode
     * @param sourceCode - The source code of the catalogue object
     * @returns {SourcePackage}
     */
    createSourcePackage(sourceCodeClassname, sourceCode) {
        if (
            typeof sourceCode === "undefined"
            || typeof sourceCodeClassname === "undefined"
        )
            throw new Error("Invalid parameters!");

        return new SourcePackage(sourceCodeClassname, sourceCode);

    }
}

export default CatalogueDataObjectFactory;

